import React,{Component} from 'react';

//1. first make a new context 
const Context = React.createContext();
const reducer =(state,action)=>
{
    //payload is just some data that has been sent along with the action
    switch(action.type)
    {
        case 'DELETE_CONTACT':return {
            ...state,
            contacts :state.contacts.filter(contact=>contact.id!== action.payload)
                };
            
            case 'ADD_CONTACT':return {
            ...state,
            contacts :[action.payload,...state.contacts]
                };
            
            
        default:return state; 
}
};

//2. provider component is where your entire data lives.
export class Provider extends Component{
     state=
        {
        
    contacts:[
       /* {
            id:1,
            name:'Priyanka',
            email:'pika@gmail.com',
            phone:'9450465058'
        },
        {
            id:2,
            name:'Ashmeet',
            email:'ash@gmail.com',
            phone:'2222222222'
        },
        {
            id:3,
            name:'Harsh',
            email:'harsh@gmail.com',
            phone:'5555555555'
        }, */    
    ],
         
         dispatch :action=>
         {
         this.setState(state=>reducer(state,action))
     }
    };

render()
{
    return(
        <Context.Provider value={this.state}>
        {this.props.children}
        </Context.Provider>
        )
}
}

export const Consumer = Context.Consumer;
