import React,{Component} from 'react';
import PropTypes from 'prop-types';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Consumer} from '../context';

class Contact extends Component {
    
    constructor()
    {
          super();  
        this.state={
            
            showContactInfo : false
        };
        //this.onshowClick() = this.onshowClick.bind(this);
    }
    

//if you do not use arrow function, this keyword won't work for just onshowClick(){}
  onShowClick=(e)=>
    {
      this.setState({showContactInfo:!this.state.showContactInfo});
        console.log(e);
    };
 onDeleteClick=(e)=>
    {
      
    };
  render() {
      
      const {name,email,phone} =this.props.contact;
      const {showContactInfo} = this.state;
      //or even using this.props.branding also works, but destructuring makes the code cleaner.
        return(

            <div className="card card-body mb-3">
            <h4>{name}{' '}
            <i 
            onClick={this.onShowClick}
            className="fa fa-sort-desc"></i>
            <i class="fa fa-times" style={{cursor:'pointer',float:'right',color:'red'}}
            onClick={this.onDeleteClick} ></i>
            </h4>

            {showContactInfo ? (
            <ul className="list-group">
            <li className='list-group-item'>Email : {email}</li>
            <li className='list-group-item'>Phone : {phone}</li>
            </ul>
            ):null}

            </div>
        )
      
  }
    
    
}

//note default props is defined outside the class.
Contact.defaultProps={
        name:'Priya',
        phone:'9450465058'
    }

Contact.propTypes ={
    
    contact:PropTypes.object.isRequired,
}
export default Contact;
